    //         profiles: testData,
    //     }
    // }

    //the shorter syntax for the above
    state = {
        profiles: testData
    }


    render() {
        return (
            <div>
                <div className="header">{this.props.title}</div>
                <Form />
                <CardList profiles={this.state.profiles}/>
            </div>
        );
    }
}

export default App;
